mapper.select:
select id, user_id, expert_name, expert_discription_short, expert_portrait_url, expert_wechat_number, expert_experience, expert_tel_number, motto, expert_display_pic_list, expert_discription_detail, delete_flag, date_format(insert_time,'%Y-%m-%d') as insert_time, date_format(update_time,'%Y-%m-%d') as update_time, (select type_value from tb_expert_type t where t.id=et.top_title) as top_title from tb_expert_top et
top_title = #{type,jdbcType=VARCHAR} and city_code = #{cityCode,jdbcType=VARCHAR}
order by expert_experience and insert_time
