mapper.select:
select  
from inn_goods G

	status =0 and id = #{id,jdbcType=VARCHAR}

select from inn_goods G
status=0
and city_code=#{cityCode,jdbcType=VARCHAR}
and is_hot=#{isHot,jdbcType=VARCHAR}
group by product_id order by up_time desc,is_new,is_hot

	select  
 from inn_goods G
 
 	status=0 
 	
 		and city_code=#{cityCode,jdbcType=VARCHAR}
 	
 	
 		and product_id in( select id from inn_product where brand_id=#{brandId,jdbcType=VARCHAR})
 	
 
 order by ${sort}

select from inn_goods G
status=0 and product_id in( select id from inn_product where brand_id=#{brandId,jdbcType=VARCHAR})
and city_code=#{cityCode,jdbcType=VARCHAR}

